example_fetch:
fetch("{{ENDPOINT}}", {
  method: "POST",
  headers: { "Content-Type": "application/json", "Accept": "application/json" },
  body: JSON.stringify({
    guest_name: "Alice Smith",
    guest_email: "alice@example.com",
    attending: "yes",
    dietary: "None"
  })
}).then(res => res.json());
